import React, { useState, useRef, useEffect } from 'react';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = {
      type: 'user',
      content: inputValue.trim()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:8000/story', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: userMessage.content }),
      });

      const data = await response.json();

      if (response.ok) {
        const aiMessage = {
          type: 'ai',
          content: data.story
        };
        setMessages(prev => [...prev, aiMessage]);
      } else {
        setError(data.error || 'Something went wrong');
      }
    } catch (err) {
      setError('Failed to connect to server. Make sure backend is running on port 8000.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="app">
      <div className="header">
        <h1>🎭 AI Dungeon Master</h1>
        <p>Tell your story, and let the AI continue your adventure!</p>
      </div>

      {error && (
        <div className="error">
          {error}
        </div>
      )}

      <div className="chat-container">
        {messages.length === 0 && (
          <div style={{ textAlign: 'center', opacity: 0.5, padding: '50px 20px' }}>
            <p>Start your adventure! Type something like:</p>
            <p style={{ marginTop: '10px', fontStyle: 'italic' }}>
              "You wake up in a mysterious forest..."
            </p>
          </div>
        )}

        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.type === 'user' ? 'user-message' : 'ai-message'}`}
          >
            <div className="message-label">
              {msg.type === 'user' ? 'You' : '🎭 Dungeon Master'}
            </div>
            <div className="message-content">{msg.content}</div>
          </div>
        ))}

        {isLoading && (
          <div className="loading">
            The Dungeon Master is weaving your tale...
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <div className="input-container">
        <textarea
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="What happens next in your adventure..."
          disabled={isLoading}
        />
        <button onClick={sendMessage} disabled={!inputValue.trim() || isLoading}>
          Send
        </button>
      </div>

      <p style={{ textAlign: 'center', opacity: 0.5, marginTop: '10px', fontSize: '0.9em' }}>
        Press Enter to send, Shift+Enter for new line
      </p>
    </div>
  );
}

export default App;
